import { useState } from "react";

function App() {
  const randomElementFrom = (arr) => {
    return arr[Math.random * arr.length];
  };
  const randomType = () => {
    return randomElementFrom(chordTypes);
  };
  const randomNote = () => {
    return randomElementFrom(notes);
  };
  const randomModifier = () => {
    return randomElementFrom(modifiers);
  };

  const chordTypes = [
    "major",
    "minor",
    "augmented",
    "diminished",
    "dominant 7th",
    "major 7th",
    "minor 7th",
    "half-dimished 7th",
    "diminished 7th",
  ];
  const notes = ["a", "b", "c", "d", "e", "f", "g"];
  const modifiers = ["none", "sharp", "flat"];

  const [type, setType] = useState(randomType());
  const [note, setNote] = useState(randomNote());
  const [modifier, setModifier] = useState(randomModifier());

  const generateNote = () => {
    setNote(randomNote());
    setType(randomType());
    setModifier(randomModifier());
  };

  return (
    <div>
      <div>
        {note} {modifier} {type}
      </div>
      <button onClick={generateNote()}>New chord</button>
    </div>
  );
}

export default App;
