import { useState } from "react";
import Toggle from "./components/Toggle";

function App() {
  const types = ["", "-", "aug", "dim", "⁷", "maj⁷", "-⁷", "ø⁷", "o⁷"];
  const notes = ["a", "b", "c", "d", "e", "f", "g"];
  const modifiers = ["", "#", "♭"];

  const randomElementFrom = (arr) => {
    return arr[Math.floor(Math.random() * arr.length)];
  };

  const [currentTypes, setCurrentTypes] = useState(
    types.map((type) => {
      return { type: true };
    })
  );

  console.log(currentTypes);
  const [type, setType] = useState(randomElementFrom(types));
  const [note, setNote] = useState(randomElementFrom(notes));
  const [modifier, setModifier] = useState(randomElementFrom(modifiers));

  const generateNote = () => {
    setNote(randomElementFrom(notes));
    setType(randomElementFrom(types));
    setModifier(randomElementFrom(modifiers));
  };

  return (
    <div className="container m-auto grid max-w-2xl w-full">
      <div className="bg-slate-50 p-4 my-4 rounded-md place-items-center">
        <div className="text-5xl mb-4 place-items-center">
          {note}
          {modifier} {type}
        </div>
        <button
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
          onClick={generateNote}
        >
          Next chord
        </button>
      </div>
      <div className="container m-auto grid grid-cols-2">
        <Toggle>Major</Toggle>
        <Toggle>Minor</Toggle>
        <Toggle>Augmented</Toggle>
        <Toggle>Diminished</Toggle>
        <Toggle>Dominant 7th</Toggle>
        <Toggle>Major 7th</Toggle>
        <Toggle>Minor 7th</Toggle>
        <Toggle>Half diminished 7</Toggle>
        <Toggle>Diminished 7</Toggle>
      </div>
    </div>
  );
}

export default App;
